function Editor(id, sessionID) {
	jQuery("#editor").attr('id', 'crate-' + id);
	this.editor = quill;

	// Initilise the the editor content 
	//this.editor.setText('');
	if (store.get("CRATE2-" + sessionID)) {
		var doc = store.get("CRATE2-" + sessionID);
		this.editor.setContents(doc.delta, "user");
		jQuery("#title").text(doc.title);
	}


	// make title editable
	jQuery('#title').click(function() {
		jQuery('#title').attr('contenteditable', 'true');
	});


};


module.exports = Editor;

function LinkView(container, id){

    this.linkContainer = jQuery('<div>').appendTo(container)
        .css('position', 'relative')
        .css('top', '0px')
        .css('width', '60%')
        .css('z-index', '0')
        .css('opacity', '0.9')
        .attr('id','linkContainer')
        .hide();
    
    // #0 qr code modal
    var qrCodeModal = jQuery('<div>').appendTo(container)
        .attr('id', 'modalQRCode'+id)
        .attr('tabindex','-1')
        .attr('role','dialog')
        .attr('aria-labelledby','modalQRCodeLabel')
        .attr('aria-hidden', 'true')
        .addClass('modal');
    
    var qrCodeModalDialog = jQuery('<div>').appendTo(qrCodeModal)
        .addClass('modal-dialog');
    var qrCodeModalContent = jQuery('<div>').appendTo(qrCodeModalDialog)
        .addClass('modal-content text-center');
    this.qrcodeCanvas = jQuery('<div>');
    qrCodeModalContent.append(jQuery('<br>'))
        .append(this.qrcodeCanvas)
        .append(jQuery('<br>'));
    
    // #1 overall division
    this.alert = jQuery('<div>').appendTo(this.linkContainer);
    // #2 cross to close the division
    this.dismiss = jQuery('<button>').appendTo(this.alert)
        .attr('type', 'button')
        .addClass('close')
        .html('<span  aria-hidden="true">&times;</span><span class="sr-only"> '+
              'Close </span>');
    var rowContainer = jQuery('<div>').appendTo(this.alert)
        .addClass('container');
    var inputGroup = jQuery('<div>').appendTo(rowContainer)
        .addClass('input-group')
        .attr('style','margin-left: -16px;');
    this.input = jQuery('<input>').appendTo(inputGroup)
        .attr('type', 'text')
        .attr('id', 'sessionUrl')
        .attr('placeholder', 'Nothing to see here, move along.')
        .addClass('form-control');
    
    var inputGroup2 = jQuery('<span>').appendTo(inputGroup)
        .addClass('input-group-btn');

    this.qrcode = jQuery('<button>').appendTo(inputGroup2)
        .attr('aria-label', 'QR-code')
        .attr('type', 'button')
        .attr('data-target', '#modalQRCode'+id)
        .attr('data-toggle', 'modal')
        .addClass('btn btn-default')
        .html('<i class="fa fa-qrcode"></i> QR-Code');

    this.action = jQuery('<button  id="copyButton">').appendTo(inputGroup2)
        .attr('aria-label', 'Go!')
        .attr('type', 'button')
        .addClass('btn btn-default')
        .html('Go!')
        .css('z-index', '15');

    var self = this;
    this.dismiss.unbind("click").click(function(){self.linkContainer.hide();});
};

LinkView.prototype.printLink = function(link){
    this.linkContainer.show();
    this.action.html('<i class="fa fa-clipboard"></i> Copy');
    this.action.attr("aria-label", "Copy to clipboard");
    this.input.attr("readonly","readonly");
    this.input.val(link);
    this.qrcode.show();
};


LinkView.prototype.printLaunchLink = function(link){
    this.printLink(link);
    this.input.attr("placeholder",
                    "A link will appear in this field, give it to your "+
                    "friend!");
    this.action.unbind("click");
    this.qrcode.hide();
    return this.action;
};

LinkView.prototype.printAnswerLink = function(link){
    this.printLink(link);
    this.input.attr("placeholder",
                    "A link will appear in this field. Please give it "+
                    "back to your friend.");
    this.action.unbind("click");
    this.qrcode.hide();
    return this.action;
};

LinkView.prototype.askLink = function(){
    this.linkContainer.show();
    this.alert.removeClass("alert-warning").addClass("alert-info");
    this.action.html('Go!');
    this.action.attr("aria-label", "Stamp the ticket");
    this.input.removeAttr("readonly");
    this.input.val("");
    this.action.unbind("click");
    this.qrcode.hide();
};

LinkView.prototype.askLaunchLink = function(){
    this.askLink();
    this.input.attr("placeholder",
                    "Please, copy the ticket of your friend here to stamp "+
                    "it!");
    this.qrcode.hide();
    return this.action;
};

LinkView.prototype.askAnswerLink = function(){
    this.askLink();
    this.input.attr("placeholder", "Copy the stamped ticket to confirm "+
                    "your arrival in the network");
    this.qrcode.hide();
    return this.action;
};

LinkView.prototype.hide = function(){
    this.linkContainer.hide();
};



module.exports = LinkView;

const animals = require('animals');
const hash = require('string-hash');



/**
 * Marker is class for managing the marker of one user,it includes the caret, avatar, and pseudo Names.
 */

class Marker {
  /**
   * Marker Module manages the Carets, avatars, pseudo names for the different users of the document
   * @param {[string]}  origin the id of the the user
   * @param {Number}  lifeTime After this time, if no ping or Caret position is received => 
   * remove caret and avatar. if lifetime is -1 we didn't add the avatar
   * @param {[{index: index,length: 0}]}  range  range stars from index with the specified length
   * @param {[cursor module]}  cursorsp the used cursor module for quilljs
   * @param {[Boolean]}  cursor  create the caret or not. If it is from ping, it will be false else true
   * @param {Boolean} isItME  is it my caret ? true or false to disable the time if it is true
   */
  constructor(origin, lifeTime = -1, range, cursorsp, cursor, isItME = false) {
    /**
     *  origin the id of the the user
     * @type {[type]}
     */
    this.origin = origin;

    /**
     * lifeTime After this time, if no ping or Caret position is received => 
   * remove caret and avatar. if lifetime is -1 we don't add the avatar
     * @type {[type]}
     */
    this.lifeTime = lifeTime;

    /**
     * used to store last update time to detected outdated users
     * @type {Date}
     */
    this.time = new Date().getTime();

    /**
     * color r,g,b
     * @type {String}
     */
    this.color = this.getColor(this.origin);

    /**
     * color rgb(r,g,b)
     * @type {String}
     */
    this.colorRGB = 'rgb(' + this.color + ')';

    /**
     *  color rgba(r,g,b,0.5)
     * @type {String}
     */
    this.colorRGBLight = 'rgba(' + this.color + ', 0.5)';

    /**
     * auto generated pseudo name (from animals list)
     * @type {[type]}
     */
    this.animal = animals.words[hash(this.origin) % animals.words.length];

    /**
     * Anonymous + auto generated pseudo name
     * @type {String}
     */
    this.pseudoName = 'Anonymous ' +
      this.capitalize(animals.words[hash(this.origin) % animals.words.length]);

      /**
       * add or not the avatar 
       * @type {Boolean}
       */
    this.avatarAdd = false;
    
    /**
     * true for an editor, false if it is from a ping
     * @type {[type]}
     */
    this.cursor = cursor;

    if (lifeTime != -1) { // -1 => created without timer avatar cursor 
      if (!isItME) {
        /**
         * a timer that is used to check if the user is Outdated
         * @return {[type]}   [description]
         */
        this.timer = setInterval(() => this.checkIfOutdated(), 1000);
      }
      this.addAvatar();
      if (cursor) {
        this.addCursor(range);
      }
    }
  };

  /**
   * capitalize uppercase the first letter
   * @param  {[string]} s [string]
   * @return {[string]}   [String the first letter is in uppercase]
   */
  capitalize(s) {
    return s.charAt(0).toUpperCase() + s.slice(1);
  };


  /**
   * getColor for a specific id, get a unique color
   * @param  {[string]} str [the id of the user]
   * @return {[(r,g,b))]}     [the corresponding rgb color]
   */
  getColor(str) {
    var h1 = hash(str) % 206;
    var h2 = (h1 * 7) % 206;
    var h3 = (h1 * 11) % 206;
    return Math.floor(h1 + 50) + ", " + Math.floor(h2 + 50) + ", " + Math.floor(h3 + 50);
  };

  /**
   * update the time to keep the avatar and cursor if it exist
   * @param  {[{index: index,length: 0}]} range  [description]
   * @param  {[boolean]} cursor [if it is true add update the caret position]
   */
  update(range, cursor) {
    this.time = new Date().getTime();

    if (!$("#" + this.origin).length) {
      this.addAvatar();
    }

    jQuery("#" + this.origin).attr('data-toggle', 'tooltip');
    jQuery("#" + this.origin).attr('title', this.pseudoName);

    if (this.cursor == true && cursor == true) { // in the case of update, make sure that ping updates don't change the range
      Marker.cursors.moveCursor(this.origin, range);
    } else if (cursor == true) {
      this.cursor = cursor;
      this.addCursor(range);
    }
  };

  /**
   * checkIfOutdated check if the user is outdated and if it is the case remove its caret and avatar 
   */
  checkIfOutdated() {

    var timeNow = new Date().getTime();
    var dff = (timeNow - this.time);
    // if  cursor  is outdated 
    if ((timeNow - this.time) >= this.lifeTime) {
      // Remve cursor and avatar
      if (this.cursor) {
        Marker.cursors.removeCursor(this.origin);
        this.cursor = false;
      }
      this.removeAvatar();
      clearInterval(this.timer);
    } else {
      jQuery("#" + this.origin + "").css('opacity', (1 - ((timeNow - this.time) / this.lifeTime)));

    }

  }

  /*
   * addAvatar addAvatar of the user to the editor with corresponding divID
   * @param {String} divID [the id of the div where the avatars are placed]
   */
  addAvatar(divID = "#users") {
    jQuery(divID).append(this.getAvatar());
    jQuery("#" + this.origin + "").attr('data-toggle', 'tooltip');
    jQuery("#" + this.origin + "").attr('title', this.pseudoName);
    this.avatarAdd = true;
  };

  /**
   * getAvatar return the div that contains this user id
   * @return {[type]} [description]
   */
  getAvatar() {
    return '<div id="' + this.origin + '"style="background-color:' + this.colorRGB + ';"><img class="imageuser" src="./icons/' + this.animal + '.png" alt="' + this.pseudoName + '"></div>';
  };

  /**
   * removeAvatar remove the avatar of the user from the interface
   * @return {[type]} [description]
   */
  removeAvatar() {
    jQuery("#" + this.origin + "").remove();
    this.avatarAdd = false;
  };

  /**
   * setPseudo set pseudo  for the user
   * @param {[type]} Pseudo [description]
   */

  setPseudo(Pseudo) {
    this.pseudoName = Pseudo;
    jQuery("#" + this.origin + "").attr('title', this.pseudoName);
  };

  /**
   * addCursor add the cursor to the editor
   * @param {[{index: index,length: 0}]} range [description]
   */
  addCursor(range) {
    this.cursor = true;
    Marker.cursors.setCursor(this.origin, range, this.pseudoName, this.colorRGB);
  };

}

module.exports = Marker;

function StatesHeader(model, container){
    this.model = model;
    
    this.red = "#cd2626";
    this.yellow = "#eead0e";
    this.green = "#228b22";
    this.blue = "#00BFFF";
    
    this.signalingState = jQuery('<i>').appendTo("#connectionState")
        .addClass('fa fa-circle-o-notch fa-2x')
        .attr('data-trigger', 'hover').attr('data-toggle', 'popover')
        .attr('title', 'Signaling server status')
        .attr('data-html', 'true').attr('data-content', '')
        .attr('data-placement', 'bottom')
        .popover()
        .hide();

    this.networkState = jQuery('#state');
};

StatesHeader.prototype.setNetworkState = function(state){
    switch (state){
    case "connected":
        var connectedString =
            "<span class='alert-success'>Congratulations!</span> "+
            "You are connected to people, and people are "+
            "connected to you. <span class='alert-info'>You can start editing "+
            "together</span>.";
        this.networkState.css("color", this.green);
        this.networkState.attr("data-content", connectedString);
        break;
    case "partiallyConnected":
        var partiallyConnectedString =
            "<span class='alert-warning'>Partially "+
            "connected</span>: either you are connected to people, or people "+
            "are connected to you. "+
            "This is not great, but you <span class='alert-info'> still can "+
            "edit.</span>";
        this.networkState.css("color", this.green);
        this.networkState.attr("data-content", partiallyConnectedString);
        break;
    case "disconnected":
        var disconnectedString =
            "<span class='alert-danger'>Disconnected</span>:"+
            " you are currently editing <span class='alert-info'>on"+
            " your own</span>.";
        this.networkState.css("color", this.red);
        this.networkState.attr("data-content", disconnectedString);
        break;
    };
};

StatesHeader.prototype.setSignalingState = function(state){
    var self = this;
    function blink(){
        self.signalingState.show();
        setTimeout( function(){
            if (self.model.signaling.startedSocket){
                blink();
            } else {
                self.setSignalingState("done");
            };
        }, 1000);
    };
    
    switch (state){
    case "waitSignaling":
        this.signalingState.show();
        this.signalingState.removeClass("fa-spin");
        this.signalingState.css("color", this.yellow);
        var waitSignalingString = "<span class='alert-warning'>Connecting"+
            "</span>: establishing a connection with the signaling server. "+
            "The latter allows people to join the editing session by using "+
            "the provided link. "+
            "<i>If this state persists, consider reloading the page.</i>";
        this.signalingState.attr("data-content", waitSignalingString);
        blink();
        break;
    case "waitSharer":
        this.signalingState.show();
        this.signalingState.addClass("fa-spin");
        this.signalingState.css("color", this.blue);
        var waitSharerString = "The connection to the signaling server has "+
            "been successfully established! <span class='alert-info'>Waiting "+
            "for the sharer now</span>.";
        this.signalingState.attr("data-content", waitSharerString);
        blink();
        break;
    case "waitJoiners":
        this.signalingState.css("color", this.blue);
        this.signalingState.addClass("fa-spin");
        var waitJoinersString = "The connection to the signaling server has "+
            "been <span class='alert-success'>successfully</span> "+
            "established! "+
            "The server allows people to join the editing session by using "+
            "the provided link. "+
            "<span class='alert-info'>Waiting for the collaborators</span>."
        this.signalingState.attr("data-content", waitJoinersString);
        blink();
        break;
    case "done":
        this.signalingState.show();
        this.signalingState.removeClass("fa-spin");
        var doneString = "The connection to the signaling server has been "+
            "<span class='alert-info'>terminated</span>.";
        this.signalingState.attr("data-content", doneString);
        this.signalingState.css("color", this.green);
        this.signalingState.fadeOut(6000, "linear");
        break;
    };
};

module.exports = StatesHeader;

# jquery-crate

<i>Keywords: jQuery plugin, distributed, decentralized, collaborative editor </i>

The project aims to create a very easy-to-integrate distributed collaborative
editor directly inside your web pages.


# Usage

## Import
```html
<!-- #1 include style dependencies in the header //-->
<link rel='stylesheet' href='path/to/bootstrap.min.css' />
<link rel='stylesheet' href='path/to/font-awesome.min.css' />
```

```html
<!-- #1 include jquery, bootstrap, zeroclipboard, and the crate plugin //-->
<script src='path/to/jquery.min.js'></script>
<script src='path/to/bootstrap.min.js'></script>
<script src='path/to/ZeroclipBoard.min.js'></script>

<script src='path/to/jquery-crate.min.js'></script>
```

## Instantiate
```html
<!-- #2 create the division that will host the distributed editor //-->
<div id='myDistributedEditor'></div>
```

```javascript
// #3A instantiate a new distributed editor into the targeted division.
// options {
//   signalingOptions: configure the signaling service to join or share the
//     document. {address: http://example.of.signaling.service.address,
//                session: the-session-unique-identifier,
//                connect: true|false }
//   webRTCOptions: configure the STUN/TURN server to establish WebRTC
//     connections.
//   styleOptions: change the default styling options of the editor.
//   name: the name of the document
//   importFromJSON: the json object containing the aformentionned options plus
//     the saved sequence. If any of the other above options are specified, the
//     option in the json object are erased by them.

$('#myDistributedEditor').cratify( options );
```

## Access

```javascript
var editor = $('myDistributedEditor').cratify(args)[0];

// #4 access to the header object
editor.header.prepend( newMenuButton1 );
editor.header.append( newMenuButton2 );

// #5 access to the underlying model of the distributed document
var uid = editor.model.uid;
var broadcast = editor.model.broadcast;
// etc.
```

# Project

The project [CRATE](https://github.com/Chat-Wane/CRATE) uses cratify to create
its distributed editors.

